	# VARIABLES --------------------------
	# Car width is fixed at 16, len fixed at 32, and height (topleft corner) is 32+32 = 64
	carspeed: .word 1 # Speed is in pixels per frame
	roadoffset: .word 0 # How much down to move the road
	carcoords: .space 1024 # Storage of all cor objects
	carnum: .word 1 # Self
	# First car object is always self
	# car objects are speed -> y value -> x value -> color, size 4
	topbuffer: .space 0x4000 # Allocating offscreen memory for vanishing cars
	tempdisplay: .space 0x4000 # 64*64*4
	bottombuffer: .space 0x4000
	
	# 0 : Game running
	# 1: Game Over
	# 2: reset
	# 3 : bonus reset
	framenum: .word 0 # Frame number
	lives: .word 3

.text
	j main	
	
	# FUNCTIONS ---------------------
	handle_restart: li $t0, 3 # Add lives
	sw $t0, lives
	
	sw $zero, gamemode # Start game
	j main
	handle_up: lw $t0, carspeed
	addi $t0, $t0, 1
	sw $t0, carspeed
	jr $ra
	
	handle_down: lw $t0, carspeed
	addi $t0, $t0, -1
	beqz $t0, handle_down_exit
	sw $t0, carspeed
	handle_down_exit: jr $ra
	
	handle_right: lw $t0, carcoords+8 # x value
	addi $t0, $t0, 2
	sw $t0, carcoords+8
	jr $ra
	
	handle_left: lw $t0, carcoords+8
	addi $t0, $t0, -2
	sw $t0, carcoords+8
	jr $ra
	
	getrowcol: lw $t1, 0($sp)
	sub $t1, $t1, $gp # t1 is offset from base
	divu $t1, $t1, 4 # Convert addresses to pixels
	li $t2, 0x100 # 256
	div $t1, $t2
	mflo $t3 # row
	mfhi $t4 # col
	sw $t3, 0($sp) # Load row first
	sw $t4, -4($sp) # Then column
	addi $sp, $sp, -4
	jr $ra
	
	drawcar: lw $t6, 0($sp) # Takes arguments addr to car obejct pointer (speed -> y value -> x value -> color)
	addi $sp, $sp, -4
	lw $t0, 12($t6) # color
	lw $t1, 8($t6) # x
	addi $t1, $t1, -4 # Defining coord from tl corner -> tmiddle point
	lw $t2, 4($t6) # y
	
	# Make sure car is on screen (even in part)
	blt $t2, -16, drawcar_end
	bgt $t2, 64, drawcar_end
	 
	# Cars have a width of 9 and len of 16
	addi $t3, $t1, 9
	addi $t4, $t2, 16
	
	drawcar_rowloop: beq $t3, $t1, drawcar_end
		lw $t2, 4($t6) # Reset inner loop counter
		drawcar_colloop: beq $t2, $t4, drawcar_endcolloop
			# Get address of pixel
			li $t7, 64 # Const screen diansion
			mul $t5, $t2, $t7
			add $t5, $t5, $t1
			li $t7, 4 # Size of pixel
			mul $t5, $t5, $t7
			la $t7, tempdisplay
			add $t5, $t5, $t7
			sw $t0, 0($t5) # Write color
			
			addi $t2, $t2, 1
			j drawcar_colloop
		drawcar_endcolloop: addi $t1, $t1, 1
		j drawcar_rowloop
	drawcar_end: jr $ra 
	
	

	# MAIN ------------------------
	.globl main
	main:
	# Create self car object
	lw $t1, selfcolor
	li $t2, 32
	li $t3, 46
	sw $zero, carcoords+0 # Speed
	sw $t3, carcoords+4 # y value
	sw $t2, carcoords+8 # x value
	sw $t1, carcoords+12 # color
	
	# Branch to code depending on gamemode
	mainloop:li $v0, 32
	li $a0, 100 # 5 fps
	syscall
	 
	lw $t0, gamemode
	beq $t0, 0, gameloop
	beq $t0, 1, gameover
	beq $t0, 2, reset
	beq $t0, 3, bonus_reset
	j exit
	
	gameloop: li $t0, 0xffff0000 # First check if key is presed
		lw $t2, framenum # Update framenum
		addi $t2, $t2, 1
		sw $t2, framenum
	
		lw $t1, 0($t0) # s1 is 1 if key is pressed
		bne $t1, 1, draw # No key pressed
		lw $s0, 4($t0) # s0 is key pressed
		
		la $ra, draw # Jump to draw after handling
		beq $s0, 0x61, handle_left # A
		beq $s0, 0x64, handle_right # D
		beq $s0, 0x77, handle_up # W
		beq $s0, 0x73, handle_down # S
		
		# We no longer need s0
		
		draw: lw $t2, roadoffset # Update roadoffset
		lw $t3, carspeed
		add $t2, $t2, $t3
		sw $t2, roadoffset
		
		la $s1, tempdisplay # Pixel Counter
		addi $s2, $s1, 0x4000 # End case

		roadloop: beq $s1, $s2, roadloop_exit
			li $s5, 0x808080 # Grey, s4 is final stored color
			
			move $t1, $s1 # Kinda copypasted getrowcol for speed
			la $t2, tempdisplay
			sub $t1, $t1, $t2 # t1 is offset from base
			divu $t1, $t1, 4 # Convert addresses to pixels
			li $t2, 0x40 # 64
			div $t1, $t2
			mflo $s3 # row
			mfhi $s4 # col
			
			
			# Need to paint lines
			
			beq $s4, 32, yellowline # If col is 126, yellow
			beq $s4, 16, whiteline
			beq $s4, 48, whiteline
			j drawwrite
			
			yellowline: li $s5 0xffff00
			j drawwrite
			
			whiteline: lw $t2, roadoffset # Load roadoffset
			li $t0, 6
			div $s3, $t0
			mfhi $t1 # Remainder
			div $t2, $t0
			mfhi $t3 # Remainder
			beq $t1, $t3, drawwrite # dont color if multiple of 6 - spaces
			li $s5 0xffffff
			j drawwrite
			
			drawwrite: sw $s5, 0($s1)
			addi $s1, $s1, 4
			j roadloop
			
		roadloop_exit: lw $t0, framenum
		li $t1, 15 # New car every 15 frames
		div $t0, $t1
		mfhi $t3
		bnez $t3, drawcars
		
		# Lets make a new car!
		lw $s0, carnum # First calculate address
		la $t1, carcoords
		mul $t2, $s0, 16 # 4 words
		add $s1, $t2, $t1 # s1 is starting coords
		
		li $v0, 42 # Direction
		li $a0, 0
		li $a1, 2
		syscall
		move $s2, $a0
		mul $s2, $s2, 2 # Funny linear transform
		addi $s2, $s2, -1 # s2 is -1 if left and moving down, 1 if right and moving up
		
		li $v0, 42 # Speed
		li $a0, 0
		li $a1, 4
		syscall
		move $s3, $a0
		addi $s3, $s3, 1 # Acceptable range is 1-4
		mul $s3, $s3, $s2 # Apply direction
		
		li $v0, 42 # Color
		li $a0, 0
		li $a1, 3
		syscall
		mul $t0, $a0, 4 # index to offeset
		lw $s4, carcolors($t0) # s4 is color
		
		li $v0, 42 # X offset (from center)
		li $a0, 0
		li $a1, 24
		syscall
		addi $s5, $a0, 4 # s5 is offset
		
		beq $s2, -1, rightside
		j leftside
		
		leftside: li $s6 -16 # y
		li $t0, 32
		sub $s7, $t0, $s5
		j endlr_branch
		
		rightside: li $s6 64
		li $t0, 32
		add $s7, $t0, $s5 # 64 - s5
		
		endlr_branch: # Add new car
		sw $s3, 0($s1)
		sw $s6, 4($s1)
		sw $s7, 8($s1)
		sw $s4, 12($s1)
		
		addi $s0, $s0, 1 # Update carnum
		sw $s0, carnum
		
		
		drawcars: li $s0, 0 # Draw can with thier updated positions
		lw $s1, carnum
		drawcars_loop: beq $s0, $s1, drawlives
			addi $sp, $sp, 4 # Add address
			mul $t1, $s0, 16 # 4 words
			la $t0, carcoords($t1)
			sw $t0, 0($sp)
				
			beqz $s0, drawcar_execute # Dont change y value if that car is self
			lw $t1, 4($t0) # y value
			lw $t2, 0($t0) # speed
			lw $t3, carspeed # my speed
			add $t1, $t1, $t2 # apply effects of thier speed
			add $t1, $t1, $t3 # apple effects of my speed
			sw $t1, 4($t0) # store y value
			
			drawcar_execute: jal drawcar
		
			addi $s0, $s0, 1
		j drawcars_loop
		
		drawlives: li $t0, 0
		lw $t1, lives
		lw $s0, selfcolor
		lives_loop: beq $t0, $t1, drawscore
			mul $t2, $t0, 8 # 2 ^ 4 offest
			addi $t2, $t2, 0x100
			sw $s0, tempdisplay($t2)
			
			addi $t0, $t0, 1
			j lives_loop
		
		drawscore: li $t0, 0
		lw $t1, framenum
		div $t1, $t1, 30 # 1 pixel every 30 frames
		bne $t1, 10, game_notfinished
			li $t6, 2
			sw $t6, lives
			li $t7, 3
			sw $t7, gamemode
		game_notfinished: li $s0, 0xa52a2a # Brown
		score_loop: beq $t0, $t1, hit_detection
			mul $t2, $t0, 4 # 4 offest
			sw $s0, tempdisplay($t2)
			
			addi $t0, $t0, 1
			j score_loop
		
		hit_detection: lw $t0, carcoords+8 # x
		bge $t0, 60, hit_detected
		ble $t0, 2, hit_detected
		
		li $s0, 1 # Dont want to detect self
		lw $s1, carnum
		hitd_loop: beq $s0, $s1, hitd_end
			li $t2, 0 # Checks, 2 means complete - x and y
			mul $t1, $s0, 16 # 4 words
			la $t1, carcoords($t1) # t1 is address of current carobj
			
			lw $t3, carcoords+4 # self y
			lw $t4, 4($t1) # curr y
			sub $t5, $t4, $t3
			mul $t5, $t5, $t5 # Sqaure to make positive - abs is cringe
			bge $t5, 0x100, y_nohit # 0x100 is 16^2 in hex
			addi $t2, $t2, 1 # y hit
			
			y_nohit: lw $t3, carcoords+8 # self x
			lw $t4, 8($t1) # curr x
			sub $t5, $t4, $t3
			mul $t5, $t5, $t5 # Sqaure to make positive - abs is cringe
			bge $t5, 81, x_nohit # 9^2
			addi $t2, $t2, 1 # x hit
			
			x_nohit: beq $t2, 2, hit_detected # x and y hit
			
			addi $s0, $s0, 1
		j hitd_loop
		
		hitd_end: j writescreen
		
		hit_detected: li $t0, 2 # reset
		sw $t0, gamemode
		
		writescreen: move $t0, $gp
		la $t1, tempdisplay
		addi $t2, $t1, 0x4000
		
		writescreen_loop: beq $t1, $t2, refresh
			lw $t3, 0($t1)
			sw $t3, 0($t0)
			addi $t0, $t0, 4
			addi $t1, $t1, 4
			j writescreen_loop
		
		
		refresh:
		j mainloop
	

	gameover: move $t0, $gp
		la $t1, gameoverscreen
		addi $t2, $t1, 0x4000
		
		gameoverscreen_loop: beq $t1, $t2, gameoverscreen_exit
			lw $t3, 0($t1)
			srl $t3, $t3, 2 # Weird rgb formatting
			sw $t3, 0($t0)
			addi $t0, $t0, 4
			addi $t1, $t1, 4
			j gameoverscreen_loop
		
		gameoverscreen_exit: li $t0, 0xffff0000 # First check if key is presed
		lw $t1, 0($t0) # s1 is 1 if key is pressed
		bne $t1, 1, gameover_end # No key pressed
		lw $s0, 4($t0) # s0 is key pressed
		
		beq $s0, 0x71, handle_restart # Q, will just to main if branched
	gameover_end: j mainloop
	
	reset: li $t0, 1 # Reset speed to 1
	sw $t0, carspeed
	sw $t0, carnum # delete other cars
	
	lw $t0, lives
	addi $t0, $t0, -1
	bnez $t0, continue
	sw $zero, framenum # Reset score
	li $t1, 1 # no lives, game over
	sw $t1, gamemode
	j mainloop
	
	continue: sw $t0, lives
	sw $zero, gamemode # Now running again
	j main
	
	bonus_reset: 
	li $t3, 0xffa500
	sw $t3, selfcolor
	li $t0, 10 # Reset speed to 10
	li $t2, 1
	sw $t0, carspeed
	sw $t2, carnum # delete other cars
	
	lw $t0, lives
	addi $t0, $t0, -1
	bnez $t0, continue
	sw $zero, framenum # Reset score
	li $t1, 1 # no lives, game over
	sw $t1, gamemode
	j mainloop
	
	bonus_continue: sw $t0, lives
	sw $zero, gamemode # Now running again
	j main
	 
	exit: li $v0, 10
	syscall
